// BookStack Webhook-Daten fuer RAG-Ingestion vorbereiten
const body = $input.first().json.body || $input.first().json;

// BookStack sendet: related_item (page/chapter/book)
const item = body.related_item || body;
const text = item.html || item.text || item.markdown || item.description || '';
const name = item.name || item.title || 'Unbenannt';
const itemId = item.id || '';
const itemSlug = item.slug || '';

// HTML-Tags entfernen fuer sauberes Embedding
const cleanText = text.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();

if (!cleanText || cleanText.length < 50) {
  // Zu kurz fuer RAG-Aufnahme
  return [{ json: { skip: true, reason: 'Text zu kurz' } }];
}

return [{
  json: {
    text: `${name}\n\n${cleanText}`,
    source_type: 'wiki',
    source_id: `bookstack_${itemId}`,
    metadata: {
      title: name,
      bookstack_id: itemId,
      slug: itemSlug,
      event: body.event || 'page_update',
      updated_at: body.triggered_at || new Date().toISOString()
    },
    chunk_size: 512,
    chunk_overlap: 50
  }
}];
